package com.gishere.aicamera.controller;

import cn.hutool.json.JSONObject;
import cn.hutool.json.JSONUtil;
import com.gishere.aicamera.config.mqtt.pub.MessageSender;
import com.gishere.aicamera.config.mqtt.pub.PubTopicEnum;
import com.gishere.aicamera.config.mqtt.pub.service.ServiceFunctionEnum;
import com.gishere.aicamera.config.mqtt.pub.service.ServiceMessageInfo;
import com.gishere.aicamera.config.mqtt.pub.service.payload.*;
import com.gishere.aicamera.config.mqtt.pub.service.payload.PhotoLibImgAddPayloadDetail.UserInfoBean;
import com.gishere.aicamera.config.mqtt.sub.provider.ServerSyncProvider;
import lombok.extern.slf4j.Slf4j;
import org.springframework.data.redis.core.StringRedisTemplate;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import java.io.IOException;
import java.util.ArrayList;
import java.util.List;
import java.util.UUID;

/**
 * 测试类
 *
 * @author niXueChao
 * @date 2021/2/18.
 */
@Slf4j
@RestController
@RequestMapping("/ipc/photo/lib")
public class IpcPhotoLibTestController {

    private final MessageSender messageSender;
    private final StringRedisTemplate redisTemplate;

    public IpcPhotoLibTestController(MessageSender messageSender, StringRedisTemplate redisTemplate) {
        this.messageSender = messageSender;
        this.redisTemplate = redisTemplate;
    }

    /**
     * 底库列表
     */
    @GetMapping("/list")
    public Object libListQuery() {
        String requestId = sendServiceMsg("0697AX11410010079L", ServiceFunctionEnum.photo_lib_info, new PhotoLibInfoPayloadDetail());
        return getResponseResultByRequestId(requestId);
    }

    /**
     * 新增底库
     */
    @GetMapping("/add")
    public Object createLib() {
        PhotoLibCreatePayloadDetail detail = new PhotoLibCreatePayloadDetail();
        detail.setLibid("0002");
        detail.setName("测试2");
        detail.setThreshold(75);
        String requestId = sendServiceMsg("0697AX11410010079L", ServiceFunctionEnum.photo_lib_create, detail);
        return getResponseResultByRequestId(requestId);
    }

    /**
     * 删除底库
     */
    @GetMapping("/delete")
    public Object deleteLib() {
        PhotoLibDeletePayloadDetail detail = new PhotoLibDeletePayloadDetail();
        String requestId = sendServiceMsg("0697AX11410010079L", ServiceFunctionEnum.photo_lib_delete, detail);
        return getResponseResultByRequestId(requestId);
    }

    /**
     * 更新底库
     */
    @GetMapping("/update")
    public Object updateLib() {
        PhotoLibUpdatePayloadDetail detail = new PhotoLibUpdatePayloadDetail();
        detail.setLibid("0001");
        detail.setName("测试1");
        detail.setThreshold(88);

        String requestId = sendServiceMsg("0697AX11410010079L", ServiceFunctionEnum.photo_lib_update, detail);
        return getResponseResultByRequestId(requestId);
    }

    /**
     * 添加图片
     */
    @GetMapping("/img/add")
    public Object addPhoto() throws IOException {
        String img = "";

        List<String> imgs = new ArrayList<>();
        imgs.add(img);

        UserInfoBean userInfoBean = new UserInfoBean();
        userInfoBean.setUid("1111111111");
        userInfoBean.setName("张三");
        userInfoBean.setBase64(imgs);
        userInfoBean.setExtra(new ArrayList<>());

        PhotoLibImgAddPayloadDetail detail = new PhotoLibImgAddPayloadDetail();
        detail.setLibid("0001");
        detail.setUser_info(userInfoBean);

        String requestId = sendServiceMsg("0697AX11410010079L", ServiceFunctionEnum.photo_lib_img_add, detail);
        return getResponseResultByRequestId(requestId);
    }

    /**
     * 删除图片
     */
    @GetMapping("/img/delete")
    public Object deletePhoto() {
        PhotoLibImgDeletePayloadDetail detail = new PhotoLibImgDeletePayloadDetail();
        detail.setLibid("0001");
        detail.setUid("1111111111");
        String requestId = sendServiceMsg("0697AX11410010079L", ServiceFunctionEnum.photo_lib_img_delete, detail);
        return getResponseResultByRequestId(requestId);
    }

    /**
     * 删除所有图片(保留除图片外的信息)
     */
    @GetMapping("/img/deleteAll")
    public Object deleteAllPhoto() {
        PhotoLibImgDeleteAllPayloadDetail detail = new PhotoLibImgDeleteAllPayloadDetail();
        detail.setLibid("0001");
        String requestId = sendServiceMsg("0697AX11410010079L", ServiceFunctionEnum.photo_lib_img_delete_all, detail);
        return getResponseResultByRequestId(requestId);
    }

    /**
     * 图片详情
     */
    @GetMapping("/img/info")
    public Object imgInfo() {
        PhotoLibUserInfoPayloadDetail detail = new PhotoLibUserInfoPayloadDetail();
        detail.setLibid("0001");
        detail.setUid("1111111111");

        String requestId = sendServiceMsg("0697AX11410010079L", ServiceFunctionEnum.photo_lib_user_info, detail);
        return getResponseResultByRequestId(requestId);
    }


    /**
     * 底库相关消息发送
     *
     * @param clientId            IPC设备序列号
     * @param serviceFunctionEnum 功能
     * @param detail              请求参数详情
     * @return requestId
     */
    private String sendServiceMsg(String clientId, ServiceFunctionEnum serviceFunctionEnum, ServicePayloadDetail detail) {
        String requestId = UUID.randomUUID().toString();
        messageSender.send(ServiceMessageInfo.newBuilder()
                //发送至同步应答Topic
                .topicEnum(PubTopicEnum.PUB_SERVER_SYNC)
                .clientId(clientId)
                .functionEnum(serviceFunctionEnum)
                .requestId(requestId)
                .payloadDetail(detail)
                .build());
        return requestId;
    }


    /**
     * 获取应答
     *
     * @param requestId 请求id
     * @return IPC原始应答消息的PayLoad部分
     */
    private JSONObject getResponseResultByRequestId(String requestId) {
        String redisKey = ServerSyncProvider.IPC_SERVER_RESPONSE + ":" + requestId;
        //5秒钟内获取不到就算了
        for (int i = 0; i < 10; i++) {
            String result = redisTemplate.opsForValue().get(redisKey);
            if (result != null) {
                JSONObject obj = JSONUtil.parseObj(result);
                redisTemplate.delete(redisKey);
                return obj;
            }
            try {
                Thread.sleep(500);
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
        }
        log.warn("未获取到ipc应答");
        return null;
    }


}
